import React, { useState, useEffect } from 'react';
import './App.css';
import axios from 'axios';
import Card from './page/Card';

const App =() => {
  const [data,setData] = useState([]);
 const fecthData = async()=>{
  try{
    await axios.get('http://localhost:8000/list').then((response)=>{
      const result= response.data
      setData(result);
    })
  }catch(error){
    console.log("error",error)
  }
 }
 useEffect(()=>{
  fecthData()
 },[])

  return (
    <>
      <div>
        <br/>
        <h2 align="center">PokeApi</h2>
        <br/>
        <div className="container">
        <div className="row">
          {
            data.map((i,index)=>{
              return(
                <div className="col-lg-4 col-md-6 my-2" key={index}>
                    <div className="row" >
                    {/* <img alt="Sample" src={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/other/home/${index+1}.png`} /> */}
                    <Card id={index+1} name={i.name} />
                    <div className="card-body">
                      <h5 className="card-title " align="center" style={{ textTransform: 'uppercase'}}>{i.name}</h5>
                      <a className="btn btn-primary float-end" href={i.url}>Detail</a>
                    </div>
                  </div>
                </div>
              )
            })
          }

          </div>
          </div>
      </div>
    </>
  );
}
export default App;
