import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Card = ({id,name}) =>{
    const [detail,setDetail]=useState([])

    useEffect(()=>{
        const fecthDetail = async () => {
            try{
                await axios.get(`http://localhost:8000/detail/${id}`).then((response) =>{
                    console.log(response.data.sprites.other.dream_world.front_default);
                    const result =
                    response.data.sprites.other.dream_world.front_default;
                    setDetail(result);
                });
            } catch (error) {
                console.log("error", error);
            }
        }
        fecthDetail();
    },[])
//`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/other/home/${index+1}.png`
    return(
        <div className="card">
        <img alt={name} src={detail} height={50} width={100} />
        <div className="card-body">
            <h5 className="card-title" align="center" style={{ textTransform: 'uppercase'}}>{name}</h5>
            <a className="btn btn-primary float-end" href=''>Detail</a>
        </div>
        </div>
    )
}

export default Card;
